import "./App.css";
import { useEffect, useState } from "react";
import { Switch, Route } from "react-router-dom";

import Header from "../Header/Header";
import Home from "../Pages/Home/Home";
import Headphones from "../Pages/Headphones/Headphones";
import Speakers from "../Pages/Speakers/Speakers";
import Earphones from "../Pages/Earphones/Earphones";
import Product from "../Pages/Product/Product";
import Checkout from "../Pages/Checkout/Checkout";
import CartModal from "../CartModal/CartModal";
import InfoToolTip from "../InfoToolTip/InfoToolTip";
import Footer from "../Footer/Footer";

import ProtectedRoute from "../ProtectedRoute/ProtectedRoute";
import productsData from "../../utils/data.json";

function App() {
  const [isCartModalOpen, setIsCartModalOpen] = useState(false);
  const [isInfoToolTipOpen, setIsInfoToolTipOpen] = useState(false);

  const [headphones, setHeadphone] = useState([]);
  const [speakers, setSpeakers] = useState([]);
  const [earphones, setEarphones] = useState([]);
  const [cart, setCart] = useState(
    JSON.parse(localStorage.getItem("cart") || "[]")
  );
  const [totalPrice, setTotalPrice] = useState(0);

  const [product, setProduct] = useState({});

  const [order, setOrder] = useState({
    orderInfo: {},
    products: [],
    total: 0,

  });

  useEffect(() => {
    setHeadphone(
      productsData
        .filter((product) => product.category === "headphones")
        .reverse()
    );

    setSpeakers(
      productsData
        .filter((product) => product.category === "speakers")
        .reverse()
    );

    setEarphones(
      productsData
        .filter((product) => product.category === "earphones")
        .reverse()
    );

    calculateTotalPrice();
  }, []);

  // listeners for cart modal closing
  useEffect(() => {
    const closeByEscape = (e) => {
      if (e.key === "Escape") {
        setIsCartModalOpen(false);
      }
    };

    const closeByClick = (evt) => {
      if (
        !evt.target.classList.contains("cart-modal__container") &&
        evt.target.classList.contains("cart-modal")
      ) {
        setIsCartModalOpen(false);
      }
    };

    document.addEventListener("keydown", closeByEscape);
    document.addEventListener("click", closeByClick);

    return () => {
      document.removeEventListener("keydown", closeByEscape);
      document.removeEventListener("click", closeByClick);
    };
  }, []);

  const handleProductClick = (product) => {
    setProduct(product);
  };

  const handleCartClick = () => {
    setIsCartModalOpen(!isCartModalOpen);
  };

  const handleAddToCart = (product) => {
    if (cart.every((p) => p.name !== product.name)) {
      cart.unshift(product);
    } else {
      cart.map((p) => {
        if (p.name === product.name) {
          p.total += product.total;
          p.amount += product.amount;
        }
        return p;
      });
    }
    calculateTotalPrice();
    saveToLocalCart();
  };

  const handleCartUpdate = (product) => {
    cart.map((p) => {
      if (p.name === product.name) {
        p.amount = product.amount;
        p.total = product.amount * p.price;
      }
      return p;
    });
    calculateTotalPrice();
    saveToLocalCart();
  };

  const handleClearCart = () => {
    setCart([]);
    setTotalPrice(0);
    localStorage.setItem("cart", JSON.stringify([]));
  };

  const calculateTotalPrice = () => {
    let sum = 0;
    cart.forEach((p) => (sum += p.total));
    setTotalPrice(sum);
  };

  const saveToLocalCart = () => {
    localStorage.setItem("cart", JSON.stringify(cart));
  };

  const handlePayment = (order) => {
    setOrder(order);
    setIsInfoToolTipOpen(true);

    console.log(order);
  };

  return (
    <div className="App">
      <Header onCartClick={handleCartClick} cartItems={cart.length} />

      <Switch>
        <Route exact path={"/"}>
          <Home />
        </Route>

        <Route path={"/headphones"}>
          <Headphones
            headphones={headphones}
            onPorductClick={handleProductClick}
          />
        </Route>

        <Route path={"/speakers"}>
          <Speakers speakers={speakers} onPorductClick={handleProductClick} />
        </Route>

        <Route path={"/earphones"}>
          <Earphones
            earphones={earphones}
            onPorductClick={handleProductClick}
          />
        </Route>

        <ProtectedRoute path="/product" product={product}>
          <Product product={product} onAddToCart={handleAddToCart} />
        </ProtectedRoute>

        <Route path={"/checkout"}>
          <Checkout
            cart={cart}
            totalPrice={totalPrice}
            onPayment={handlePayment}
          />
        </Route>
      </Switch>

      <CartModal
        isOpen={isCartModalOpen}
        cart={cart}
        totalPrice={totalPrice}
        onCartUpdate={handleCartUpdate}
        onClear={handleClearCart}
      />

      <InfoToolTip isOpen={isInfoToolTipOpen} order={order} />

      <Footer />
    </div>
  );
}

export default App;
