import "./ProductCard.css";

const ProductCard = ({ card }) => {
  console.log(card);

  const checkScreen = () => {
    let tablet = window.matchMedia("(max-width: 1440px)");
    let mobile = window.matchMedia("(max-width: 1440px)");
  };
  return (
    <section className="product-card">
      <div className="product_card__img"></div>
      <div className="product-card__info">
        <p className="product-card__name">{`${card.name} Headphones`}</p>
        <p className="product-card__description">{card.description}</p>
        <button className="product-card__button">See Product</button>
      </div>
    </section>
  );
};
export default ProductCard;
