import { useEffect } from "react";
import "./ProductCard.css";

import imagePath from "../../utils/imagePath";

const ProductCard = ({ card, children }) => {
  let fileName = card.image.desktop.substring(1, card.image.desktop.length - 3);

  useEffect(() => {
    const tablet = window.matchMedia("(max-width: 1440px)");

    if (tablet.matches) {
      fileName = card.image.desktop.substring(1, card.image.tablet.length - 3);
    } else {
      fileName = card.image.mobile.substring(1, card.image.tablet.length - 3);
    }
  }, []);

  return (
    <section className="product-card">
      <img
        className="product_card__img"
        src={require("../../images" + fileName + "jpg")}
      ></img>
      <div className="product-card__info">
        <p className="product-card__name">{`${card.name} Headphones`}</p>
        <p className="product-card__description">{card.description}</p>

        {children}
      </div>
    </section>
  );
};
export default ProductCard;
