import "./Header.css";

import logo from "../../images/assets/shared/desktop/logo.svg";
import cart from "../../images/assets/shared/desktop/icon-cart.svg";
import mobile from "../../images/assets/shared/tablet/icon-hamburger.svg";

import Navigation from "../Navigation/Navigation";
import { useState } from "react";

const Header = () => {
  const [isMobile, setIsMobile] = useState(false);

  const handleMobileClick = () => {
    setIsMobile(!isMobile);
  };
  return (
    <header className="header">
      <div className="header__container">
        <div className="header__inner-container">
          <div className="header__logo-container">
            <div className="header__logo-wrap">
              <img className="header__mobile" src={mobile} alt="mobile icon"></img>
              <img className="header__logo" src={logo} alt="logo"></img>
            </div>
          </div>

          <Navigation isMobile={false} />
          <img className="header__cart-icon" src={cart} alt="cart"></img>
        </div>

        <div className="header__navigation-mobile">
          <Navigation isMobile={true} />
        </div>
      </div>
    </header>
  );
};
export default Header;
