import "./InfoToolTip.css";
import { Link } from "react-router-dom";

import success from "../../images/assets/shared/success.png";

const InfoToolTip = ({ isOpen, order }) => {
  return (
    <div className={`info ${isOpen ? "info_open" : ""}`}>
      <div className="info__container">
        <img className="info__img" src={success} alt="success"></img>

        <p className="info__title">THANK YOU FOR YOUR ORDER</p>

        <p className="info__subtitle">
          You will receive an email confirmation shortly.
        </p>

        <div className="info__summary">
          <div className="info__product-wrap">

          <li className="cart-card__item">
      <div className="cart-card__item-info">
        <img
          className="cart-card__image"
          src={require("../../../images" + image + ".jpg")}
          alt="product"
        ></img>

        <div className="cart-card__info-wrap">
          <p className="cart-card__item-name">{card.name}</p>
          <p className="cart-card__item-price">{`$ ${card.price}`}</p>
        </div>
      </div>

      {isSummary ? (
        <label
          className={`cart-card__amount ${
            isSummary ? "cart-card__amount_type_summary" : ""
          }`}
        >{`x${card.amount}`}</label>
      ) : (
        <div className="cart-card__amount-wrap">
          <label className="cart-card__amount-control" onClick={handleDecrease}>
            -
          </label>
          <label className="cart-card__amount">{card.amount}</label>
          <label className="cart-card__amount-control" onClick={handleIncrease}>
            +
          </label>
        </div>
      )}
    </li>

            <p className="info__product-rest">{`and ${
              order.products.length - 1
            } other item(s)`}</p>
          </div>

          <div className="info__grand-total">
            <p className="info__grand-title">GRAND TOTAL</p>
            <p className="info__total">{`$ ${order.grandTotal}`}</p>
          </div>
        </div>

        <Link className="info__back-button" to="/">
          BACK TO HOME
        </Link>
      </div>
    </div>
  );
};
export default InfoToolTip;
