import "./Cart.css";

import CartCard from "../CartCard/CartCard";

const Cart = ({ cart }) => {
  return (
    <div className="cart">
      <div className="cart__title-container">
        <p className="cart__title">{`cart (${cart.length})`}</p>
        <p className="cart__remove">Remove all</p>
      </div>

      <ul className="cart__items">
        {cart.map((product, id) => (
          <CartCard key={id} card={product} />
        ))}
      </ul>

      <div className="cart__total-container">
        <div className="cart__total-description-wrap">
          <p className="cart__total-description">TOTAL</p>
          <p className="cart__total-price">{cart.total}</p>
        </div>
      </div>

      <button className="cart__button">checkout</button>
    </div>
  );
};
export default Cart;
