import "./Product.css";
import { useHistory } from "react-router-dom";

import ProductsCard from "../../ProductCard/ProductCard";
import { useState } from "react";

const Product = ({ earphones }) => {
  const [amount, setAmount] = useState("1");

  let history = useHistory();

  return (
    <section className="product">
      <div className="product__container">
        <label className="product__back" onClick={history.goBack}>
          go back
        </label>
        {earphones.map((card, id) => (
          <ProductsCard key={id} card={card}>
            <div className="product__cart-container">
              <p className="product__price">$ 2,999</p>

              <div className="product__add-container">
                <div className="product__amount-wrap">
                  <label className="product__amount-control">-</label>
                  <label className="product__amount">1</label>
                  <label className="product__amount-control">+</label>
                </div>

                <button className="product__add">add to cart</button>
              </div>
            </div>
          </ProductsCard>
        ))}
      </div>
    </section>
  );
};
export default Product;
