const imagePath = (path) => {
  const tablet = window.matchMedia("(max-width: 1440px)");
  const mobile = window.matchMedia("(max-width: 768px)");

  if (tablet.matches) {
    return path.tablet.substring(1, path.tablet.length - 3);
  } else if (mobile.matches) {
    return path.mobile.substring(1, path.mobile.length - 3);
  } else {
    return path.desktop.substring(1, path.mobile.length - 3);
  }
};
export default imagePath;
