import "./Product.css";
import { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";

import ProductsCard from "../../ProductCard/ProductCard";
import CategoryCards from "../../CategoryCards/CategoryCards";
import About from "../../About/About";

const Product = ({ product }) => {
  const [amount, setAmount] = useState(1);

  let firstFileName = product.gallery.first.desktop.substring(
    1,
    product.gallary.desktop.length - 3
  );

  let secondFileName = product.gallary.second.desktop.substring(
    1,
    product.gallary.desktop.length - 3
  );

  let thirdFileName = product.gallary.third.desktop.substring(
    1,
    product.gallary.desktop.length - 3
  );

  let history = useHistory();

  useEffect(() => {
    const tablet = window.matchMedia("(max-width: 1440px)");

    if (tablet.matches) {
      firstFileName = product.gallary.tablet.substring(
        1,
        product.gallary.tablet.length - 3
      );

      secondFileName = product.gallary.second.tablet.substring(
        1,
        product.gallary.tablet.length - 3
      );

      thirdFileName = product.gallary.third.tablet.substring(
        1,
        product.gallary.tablet.length - 3
      );
    } else {
    }
  }, []);

  console.log(product);

  const handleIncrease = () => {
    setAmount(amount + 1);
  };

  const handleDecrease = () => {
    if (amount - 1 <= 0) return;
    setAmount(amount - 1);
  };

  return (
    <section className="product">
      <div className="product__container">
        <label className="product__back" onClick={history.goBack}>
          Go Back
        </label>

        <ProductsCard card={product}>
          <div className="product__cart-container">
            <p className="product__price">{`$ ${product.price}`}</p>

            <div className="product__add-container">
              <div className="product__amount-wrap">
                <label
                  className="product__amount-control"
                  onClick={handleDecrease}
                >
                  -
                </label>
                <label className="product__amount">{amount}</label>
                <label
                  className="product__amount-control"
                  onClick={handleIncrease}
                >
                  +
                </label>
              </div>

              <button className="product__add">add to cart</button>
            </div>
          </div>
        </ProductsCard>

        <div className="product__info-wrap">
          <div className="product__features">
            <p className="product__features-title">FEATURES</p>
            <p className="product__features-text">{product.features}</p>
          </div>

          <div className="product__box">
            <p className="product__features-title">in the box</p>
            <ul className="product__box-list">
              {product.includes.map((item, key) => (
                <li className="product__box-item" key={key}>
                  <p className="product__box-quantity">{item.quantity}x</p>
                  <label className="product__box-item-name">{item.item}</label>
                </li>
              ))}
            </ul>
          </div>
        </div>

        <div className="product__gallary">
          <img
            className="product__gallary-img"
            src={require("../../images" + firstFileName + "jpg")}
            alt=" gallary image"
          ></img>

          <img
            className="product__gallary-img"
            src=""
            alt=" gallary image"
          ></img>

          <img
            className="product__gallary-img"
            src=""
            alt=" gallary image"
          ></img>
        </div>

        <div className="categories">
          <CategoryCards />
        </div>

        <div className="about">
          <About />
        </div>
      </div>
    </section>
  );
};
export default Product;
