import "./App.css";
import { useEffect, useState } from "react";
import { Switch, Route } from "react-router-dom";

import Header from "../Header/Header";
import Home from "../Pages/Home/Home";
import Headphones from "../Pages/Headphones/Headphones";
import Speakers from "../Pages/Speakers/Speakers";
import Earphones from "../Pages/Earphones/Earphones";
import Product from "../Pages/Product/Product";
import cartModal from "./"
import Footer from "../Footer/Footer";

import productsData from "../../vendor/data.json";

function App() {
  const [headphones, setHeadphone] = useState([]);
  const [speakers, setSpeakers] = useState([]);
  const [earphones, setEarphones] = useState([]);

  const [product, setProduct] = useState({});

  useEffect(() => {
    setHeadphone(
      productsData
        .filter((product) => product.category === "headphones")
        .reverse()
    );

    setSpeakers(
      productsData
        .filter((product) => product.category === "speakers")
        .reverse()
    );

    setEarphones(
      productsData
        .filter((product) => product.category === "earphones")
        .reverse()
    );
  }, []);

  const handleProductClick = (product) => {
    setProduct(product);
    console.log(product);
  };

  return (
    <div className="App">
      <Header />

      <Switch>
        <Route exact path={"/"}>
          <Home />
        </Route>

        <Route path={"/headphones"}>
          <Headphones
            headphones={headphones}
            onPorductClick={handleProductClick}
          />
        </Route>

        <Route path={"/speakers"}>
          <Speakers speakers={speakers} onPorductClick={handleProductClick} />
        </Route>

        <Route path={"/earphones"}>
          <Earphones
            earphones={earphones}
            onPorductClick={handleProductClick}
          />
        </Route>

        <Route path={"/product"}>
          <Product product={product} />
        </Route>
      </Switch>

      <Footer />
    </div>
  );
}

export default App;
