import "./Checkout.css";
import { useEffect, useState } from "react";

import Cart from "../../Cart/Cart";
import Billing from "./Billing/Billing";
import Shipping from "./Shipping/Shipping";

import vatCalculator from "../../../utils/vatCalculator";
import useFormAndValidation from "../../../utils/useFormAndValidation";

const Checkout = ({ cart, totalPrice }) => {
  const { values, handleChange, errors, isValid, resetForm, setValues } =
    useFormAndValidation();

  const [isEmoneyPayment, setIsEmoneyPayment] = useState(false);
  const [isCashPayment, setIsCashPayment] = useState(false);

  const grandTotal = vatCalculator(totalPrice) + 50 + totalPrice;
  const shippingPrice = 50;

  useEffect(() => {
    resetForm();
    setValues({});
  }, []);

  return (
    <section className="checkout">
      <div className="checkout__container">
        <form className="checkout__form">
          <p className="checkout__title">Checkout</p>

          <Billing
            values={values}
            errors={errors}
            handleChange={handleChange}
          />

          <Shipping
            values={values}
            errors={errors}
            handleChange={handleChange}
          />

          <div className="checkout__wrap">
            <label className="checkout__label">payment details</label>

            <div className="checkout__input-row checkout__input-row_type_payment">
              <label className="checkout__input-label">Payment Method</label>

              <div className="checkout__input-column">
                <div className="checkout__radio-wrap">
                  <input
                    value="emoney"
                    type="radio"
                    name="payment"
                    className="checkout__input-radio"
                    checked={isEmoneyPayment}
                    readOnly
                    onClick={() => {
                      setIsEmoneyPayment(true);
                      setIsCashPayment(false);
                    }}
                  ></input>
                  <label className="checkout__radio-label">e-Money</label>
                </div>

                <div className="checkout__radio-wrap">
                  <input
                    type="radio"
                    name="payment"
                    className="checkout__input-radio"
                    checked={isCashPayment}
                    readOnly
                    onClick={() => {
                      setIsEmoneyPayment(false);
                      setIsCashPayment(true);
                    }}
                  ></input>
                  <label className="checkout__radio-label">
                    Cash on Delivery
                  </label>
                </div>
              </div>
            </div>

            {isEmoneyPayment ? (
              <div className="checkout__input-row">
                <div className="checkout__input-wrap">
                  <label className="checkout__input-label">
                    e-Money Number
                  </label>
                  <input
                    name="emoneyNumber"
                    required
                    pattern="[0-9]{1,20}"
                    className="checkout__input"
                    placeholder="238521993"
                    onChange={handleChange}
                    value={values.emoneyNumber || ""}
                  ></input>
                  <label className="checkout__input-label-error">
                    {errors.emoneyNumber}
                  </label>
                </div>

                <div className="checkout__input-wrap">
                  <label className="checkout__input-label">e-Money PIN</label>
                  <input
                    name="emoneyPin"
                    required
                    pattern="[0-9]{1,4}"
                    className="checkout__input"
                    placeholder="6891"
                    onChange={handleChange}
                    value={values.emoneyPin || ""}
                  ></input>
                  <label className="checkout__input-label-error">
                    {errors.emoneyPin}
                  </label>
                </div>
              </div>
            ) : (
              ""
            )}
          </div>
        </form>

        <div className="checkout__summary">
          <Cart cart={cart} title="summary">
            <div className="cart__checkout-continer">
              <div className="cart__total-container">
                <div className="cart__total-description-wrap">
                  <p className="cart__total-description">TOTAL</p>
                  <p className="cart__total-price">{`$ ${totalPrice}`}</p>
                </div>
              </div>

              <div className="cart__total-container">
                <div className="cart__total-description-wrap">
                  <p className="cart__total-description">SHIPPING</p>
                  <p className="cart__total-price">{`$ ${shippingPrice}`}</p>
                </div>
              </div>

              <div className="cart__total-container">
                <div className="cart__total-description-wrap">
                  <p className="cart__total-description">VAT (INCLUDED)</p>
                  <p className="cart__total-price">{`$ ${vatCalculator(
                    totalPrice
                  )}`}</p>
                </div>
              </div>
            </div>

            <div className="cart__total-container">
              <div className="cart__total-description-wrap">
                <p className="cart__total-description">GRAND TOTAL</p>
                <p className="cart__total-price cart__total-price_type_grand">{`$ ${grandTotal}`}</p>
              </div>
            </div>

            <button
              type="button"
              className={`cart__button ${
                isValid && (isCashPayment || isEmoneyPayment)
                  ? ""
                  : "cart__button_disabled"
              }`}
              to="/checkout"
            >
              {"CONTINUE & PAY"}
            </button>
          </Cart>
        </div>
      </div>
    </section>
  );
};
export default Checkout;
