import "./App.css";
import { useEffect, useState } from "react";
import { Switch, Route } from "react-router-dom";

import Header from "../Header/Header";
import Home from "../Pages/Home/Home";
import Headphones from "../Pages/Headphones/Headphones";
import Speakers from "../Pages/Speakers/Speakers";
import Earphones from "../Pages/Earphones/Earphones";
import Product from "../Pages/Product/Product";
import Footer from "../Footer/Footer";

import productsData from "../../vendor/data.json";

function App() {
  const [headphones, setHeadphone] = useState([]);
  const [speakers, setSpeakers] = useState([]);
  const [earphones, setEarphones] = useState([]);

  const [product, setProduct] = useState({});

  const handleProductClick = (product) => {

  };

  useEffect(() => {
    setHeadphone(
      productsData.filter((product) => product.category === "headphones")
    );

    setSpeakers(
      productsData.filter((product) => product.category === "speakers")
    );

    setEarphones(
      productsData.filter((product) => product.category === "earphones")
    );
  }, []);

  return (
    <div className="App">
      <Header />

      <Switch>
        <Route exact path={"/"}>
          <Home />
        </Route>

        <Route path={"/headphones"}>
          <Headphones headphones={headphones} />
        </Route>

        <Route path={"/speakers"}>
          <Speakers speakers={speakers} />
        </Route>

        <Route path={"/earphones"}>
          <Earphones earphones={earphones} />
        </Route>

        <Route path={"/product"}>
          <Product earphones={earphones} />
        </Route>
      </Switch>

      <Footer />
    </div>
  );
}

export default App;
