import "./CartModal.css";

import Cart from "../Cart/Cart";

const CartModal = ({ isOpen }) => {
  return (
    <section className={`cart-modal ${isOpen ? "cart-modal_open" : ""}`}>

      <div className="cart-modal__container">
        <Cart />
      </div>
    </section>
  );
};
export default CartModal;
